import { useEffect } from 'react';

const CoordinatesRefresh = () => {
    let message
    useEffect(() => {
        const socket = new WebSocket('ws://localhost:7555');

        // WebSocket event: connection established
        socket.onopen = () => {
            console.log('WebSocket connection established.');
        }

        const interval = setInterval(() => {
            socket.onmessage = (event) => {
                message = event.data;
                console.log(message);
            }
        }, 10);

        // WebSocket event: connection closed
        socket.onclose = () => {
            console.log('WebSocket connection closed.');
        };

        return () => {
            clearInterval(interval);
            socket.close();
        };
    }, []);

    return null; // or return a placeholder component if necessary
};

export default CoordinatesRefresh;
