import { useEffect, useState } from 'react';
import { Marker } from 'react-leaflet'
import L from 'leaflet';

function iconCircle() {
    return L.icon({
        iconUrl: require('../img/circle.png'),
        iconSize: new L.Point(8, 8),
    })
}

const [newCoordinates, setNewCoordinates] = useState([40.644327187923224, -8.657387495040895]);

const CoordinatesRefresh = () => {
    //const [message, setMessage] = useState("");

    useEffect(() => {
        let socket = new WebSocket('ws://localhost:7555');
        let message;

        // WebSocket event: socket open
        socket.onopen = () => {
            console.log('WebSocket connection opened.');
        }

        const interval = setInterval(() => {
            socket.onmessage = (event) => {
                message = event.data;
                console.log(message);
                const lat = parseFloat(message.split(' ')[0]);
                const lon = parseFloat(message.split(' ')[1]);
                setNewCoordinates([lat, lon]);
                console.log(newCoordinates)
            }
        }, 10);

        // WebSocket event: connection closed
        socket.onclose = () => {
            console.log('WebSocket connection closed.');
        };

        return () => {
            clearInterval(interval);
            socket.close();
        };
    }, [newCoordinates]);

    return (
        <>
            <Marker icon={iconCircle()} position={[newCoordinates]}></Marker>
        </>);

};

export default CoordinatesRefresh;
