import { useEffect, useState } from 'react';

const CoordinatesRefresh = () => {
    const [message, setMessage] = useState("");
    useEffect(() => {
        const socket = new WebSocket('ws://localhost:7555');

        // WebSocket event: socket open
        socket.onopen = () => {
            console.log('WebSocket connection opened.');
        }

        const interval = setInterval(() => {
            socket.onmessage = (event) => {
                setMessage(event.data);
                console.log(message);
            }
        }, 10);

        // WebSocket event: connection closed
        socket.onclose = () => {
            console.log('WebSocket connection closed.');
        };

        socket.onerror = function (err) {
            console.error('Socket encountered error: ', err.message, 'Closing socket');
            ws.close();
        };

        return () => {
            clearInterval(interval);
            socket.close();
        };
    }, [message]);

    return null; // or return a placeholder component if necessary
};

export default CoordinatesRefresh;
