import { useEffect, useState } from 'react';
import { Marker } from 'react-leaflet'
import L from 'leaflet';

function iconCar1() {
    return L.icon({
        iconUrl: require('../img/car.png'),
        iconSize: new L.Point(35, 35),
    })
}

const CoordinatesRefresh = () => {
    //const [message, setMessage] = useState("");
    const [newCoordinates, setNewCoordinates] = useState([40.644327187923224, -8.657387495040895]);


    useEffect(() => {
        let socket = new WebSocket('ws://localhost:7555');
        let message;
        // WebSocket event: socket open
        socket.onopen = () => {
            console.log('WebSocket connection opened.');
        }

        // Function to handle incoming messages
        const handleMessage = (event) => {
            message = event.data;
            console.log(message);
            const lat = parseFloat(message.split(' ')[0]);
            const lon = parseFloat(message.split(' ')[1]);
            setNewCoordinates([lat, lon]);
            console.log(newCoordinates);
        };

        if (socket.readyState === WebSocket.OPEN) {
            socket.onmessage = handleMessage;
        }


        // WebSocket event: connection closed
        socket.onclose = () => {
            console.log('WebSocket connection closed.');
        };

        return () => {
            clearInterval(interval);
            socket.removeEventListener("message", handleMessage);
            socket.close();
        };
    }, [newCoordinates]);

    return null;

};

export default CoordinatesRefresh;
