import { useEffect, useState } from 'react';
import { Marker } from 'react-leaflet'
import L from 'leaflet';

function iconCircle() {
    return L.icon({
        iconUrl: require('../img/circle.png'),
        iconSize: new L.Point(8, 8),
    })
}

const CoordinatesRefresh = () => {
    const [message, setMessage] = useState("");
    const [coordinates, setCoordinates] = useState([40.644327187923224, -8.657387495040895]);
    useEffect(() => {
        let socket = new WebSocket('ws://localhost:7555');

        // WebSocket event: socket open
        socket.onopen = () => {
            console.log('WebSocket connection opened.');
        }

        const interval = setInterval(() => {
            socket.onmessage = (event) => {
                setMessage(event.data);
                console.log(message);
                setCoordinates([message.split(' ')[0], message.split(' ')[1]]);
            }
        }, 10);

        // WebSocket event: connection closed
        socket.onclose = () => {
            console.log('WebSocket connection closed.');
        };

        return () => {
            clearInterval(interval);
            socket.close();
        };
    }, [message, coordinates]);

    return (
        <>
            <Marker icon={iconCircle()} position={coordinates}></Marker>
        </>);
};

export default CoordinatesRefresh;
